import "./Home.css"

function Home() {
    let parallax = document.createElement('div')

    for (let i = 1; i < 4; i++) {
        let parallaxChild = document.createElement('div')
        parallaxChild.classList.add("parallax-image", `parallax-image-${i}`)
        parallax.appendChild(parallaxChild)
    }

    parallax.addEventListener('mousemove', (event) => {
        let posX = event.clientX
        let posY = event.clientY

        let percent = -100

        for (let elem of parallax.children) {
            elem.style.transform = 'translate(' + posX / percent + 'px, ' + posY / percent +'px)'
            percent += 33
        }
    })
    
    return (
        <div className="parallax">
            {parallax.map(par => {par})}
        </div>
    )
}

export default Home
